from langflow.custom.custom_component.component import Component
from langflow.io import MessageTextInput, Output
from langflow.schema.message import Message

class ConditionalRouterComponent(Component):
    display_name = "Conditional Router"
    description = "Routes tasks based on the Orchestrator's decision."
    documentation: str = "https://docs.langflow.org/components-custom-components"
    icon = "route"
    name = "ConditionalRouter"

    inputs = [
        MessageTextInput(
            name="orchestrator_output",
            display_name="Orchestrator Output",
            info="The output from the orchestrator to determine the next agent.",
            required=True,
        ),
    ]

    outputs = [
        Output(display_name="Code Generator", name="code_generator", method="route_to_code_generator"),
        Output(display_name="Code Reviewer", name="code_reviewer", method="route_to_code_reviewer"),
        Output(display_name="Documentation Agent", name="documentation_agent", method="route_to_documentation_agent"),
        Output(display_name="Fallback Agent", name="fallback_agent", method="route_to_fallback_agent"),
    ]

    def route_to_code_generator(self) -> Message:
        if "GENERATE" in self.orchestrator_output:
            return Message(content="code_generator")
        return Message(content="")

    def route_to_code_reviewer(self) -> Message:
        if "REVIEW" in self.orchestrator_output:
            return Message(content="code_reviewer")
        return Message(content="")

    def route_to_documentation_agent(self) -> Message:
        if "DOCUMENT" in self.orchestrator_output:
            return Message(content="documentation_agent")
        return Message(content="")

    def route_to_fallback_agent(self) -> Message:
        if not any(keyword in self.orchestrator_output for keyword in ["GENERATE", "REVIEW", "DOCUMENT"]):
            return Message(content="fallback_agent")
        return Message(content="")

