from langflow.io import MessageTextInput, Output
from langflow.schema.message import Message

class CodeExecutionComponent(Component):
    display_name = "Code Execution Node"
    description = "Dynamically executes Python code to validate it."
    documentation: str = "https://docs.langflow.org/components-custom-components"
    icon = "code"
    name = "CodeExecution"

    inputs = [
        MessageTextInput(
            name="code",
            display_name="Python Code",
            info="The Python code to be executed.",
            required=True,
        ),
    ]

    outputs = [
        Output(display_name="Execution Result", name="execution_result", method="execute_code"),
    ]

    def execute_code(self) -> Message:
        try:
            # Define a safe execution environment
            safe_globals = {"__builtins__": {}}
            safe_locals = {}

            # Execute the code
            exec(self.code, safe_globals, safe_locals)

            # Collect the result from the local environment
            result = safe_locals.get("result", "No result variable defined.")
            return Message(content=str(result))
        except Exception as e:
            return Message(content=f"Error: {str(e)}")

