from langflow.custom.custom_component.component import Component
from langflow.io import MessageTextInput, Output
from langflow.schema.message import Message

class FeedbackLoopComponent(Component):
    display_name = "Feedback Loop Node"
    description = "Sends code back to the Generator if the Reviewer finds errors."
    documentation: str = "https://docs.langflow.org/components-custom-components"
    icon = "loop"
    name = "FeedbackLoop"

    inputs = [
        MessageTextInput(
            name="reviewer_feedback",
            display_name="Reviewer Feedback",
            info="The feedback from the reviewer to check for errors.",
            required=True,
        ),
        MessageTextInput(
            name="current_code",
            display_name="Current Code",
            info="The current code being reviewed.",
            required=True,
        ),
    ]

    outputs = [
        Output(display_name="Status", name="status", method="determine_status"),
        Output(display_name="Next Agent", name="next_agent", method="determine_next_agent"),
    ]

    def determine_status(self) -> Message:
        if "error" in self.reviewer_feedback.lower() or "fix" in self.reviewer_feedback.lower():
            return Message(content="retry")
        return Message(content="proceed")

    def determine_next_agent(self) -> Message:
        if "error" in self.reviewer_feedback.lower() or "fix" in self.reviewer_feedback.lower():
            return Message(content="code_generator")
        return Message(content="documentation_agent")

